public class Lab051 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// while
        // while - A, B, C
        //         // A - Init
        //        // B - Condition
        //        // C - Increment / Decrement

		
		int i = 0;
		while( i <= 10){
			System.out.println(i);
			++i;
		}
		
		int modi = 0;
		while (modi <= 10) {
			System.out.println("modi will work");
			++ modi;
		}
		// infinte
		//while(true) {
			//System.out.println("pramod");
	//	}
		
		int i1 = 10;
			while (i1 >=1)	{
				System.out.println(i1);
				--i1;
			}
	}

}
